package com.walmart.adventofcode.y2015

/**
  * http://adventofcode.com/2015/day/1
  */
object Day1 {

  /**
    * Day 1 - First Challenge
    */
  def finalFloor(input: String): Int = input.foldLeft(0)((result, direction) => nextFloor(direction, result))

  /**
    * Day 1 - Second Challenge
    */
  def firstPositionToVisitBasement(input: String): Option[Int] = {
    input.foldLeft(0, 1) {
      (result, direction) => {
        val (floor, position) = result
        nextFloor(direction, floor) match {
          case -1 => return Some(position)
          case currFloor => (currFloor, position + 1)
        }
      }
    }
    None
  }

  private def nextFloor(direction: Char, currentFloor: Int): Int = {
    direction match {
      case ')' => currentFloor - 1
      case '(' => currentFloor + 1
    }
  }

  def main(args: Array[String]): Unit = {
    println("Final Floor Visited: " + finalFloor(input))
    println("First Position To Visit Basement: " + firstPositionToVisitBasement(input))
  }

